section.programlisting:
try {
    $translate = new Zend_Translator(
        array(
            'adapter' => 'Firma_Translator_Adapter_MeinFormat',
            'content' => '/path/to/translate.xx',
            'locale'  => 'en',
            'meineoption' => 'myvalue'
        )
    );
} catch (Exception $e) {
    // Datei nicht gefunden, keine Adapter Klasse...
    // Genereller Fehler
}

$cache = Zend_Cache::factory('Core',
                             'File',
                             $frontendOptions,
                             $backendOptions);
Zend_Translator::setCache($cache);
$translate = new Zend_Translator(
    array(
        'adapter' => 'gettext',
        'content' => '/path/to/translate.mo',
        'locale'  => 'en'
    )
);

// um den Cache irgendwo später im Code zu löschen
Zend_Translator::clearCache();

$cache = Zend_Cache::factory('Core',
                             'File',
                             $frontendOptions,
                             $backendOptions);
Zend_Translator::setCache($cache);
$translate = new Zend_Translator(
    array(
        'adapter' => 'gettext',
        'content' => '/path/to/translate.mo',
        'locale'  => 'en',
        'tag'     => 'MyTag'
    )
);

// irgendwo später im Code
Zend_Translator::clearCache('MyTag');
