/* * Ausdruck: * <base href="/mypage/" /> */ <base href="<?php echo $this->baseUrl(); ?>" />
/* * Ausdruck: * <link rel="stylesheet" type="text/css" href="/mypage/css/base.css" /> */ <link rel="stylesheet" type="text/css" href="<?php echo $this->baseUrl('css/base.css'); ?>" />
