section.programlisting:
use Zend\Application\ResourceBroker;
$broker    = new ResourceBroker(array(
    'router' => array('routes' => array(
        'foo' => array(
            'route'    => '/foo/:id',
            'defaults' => array(
                'module'     => 'foo',
                'controller' => 'products',
                'action'     => 'item',
            ),
            'reqs' => array(
                'id'  => '\d+',
            ),
        ),
    )),
));

$router = $broker->load('router');
// routes will be configured per specification
