section.programlisting:
// 規則を適用するリソースを作成します

// ニュースレター
$acl->addResource(new Zend_Acl_Resource('newsletter'));

// ニュース
$acl->addResource(new Zend_Acl_Resource('news'));

// 最新ニュース
$acl->addResource(new Zend_Acl_Resource('latest'), 'news');

// お知らせ
$acl->addResource(new Zend_Acl_Resource('announcement'), 'news');

// Marketing は、ニュースレターおよび最新ニュースを公開、保存できなければなりません
$acl->allow('marketing',
            array('newsletter', 'latest'),
            array('publish', 'archive'));

// Staff (そして継承による marketing) は最新ニュースの改変ができません
$acl->deny('staff', 'latest', 'revise');

// 全員 (administrators を含む) はお知らせを保存することができません
$acl->deny(null, 'announcement', 'archive');

echo $acl->isAllowed('staff', 'newsletter', 'publish') ?
     "allowed" : "denied";
// denied となります

echo $acl->isAllowed('marketing', 'newsletter', 'publish') ?
     "allowed" : "denied";
// allowed となります

echo $acl->isAllowed('staff', 'latest', 'publish') ?
     "allowed" : "denied";
// denied となります

echo $acl->isAllowed('marketing', 'latest', 'publish') ?
     "allowed" : "denied";
// allowed となります

echo $acl->isAllowed('marketing', 'latest', 'archive') ?
     "allowed" : "denied";
// allowed となります

echo $acl->isAllowed('marketing', 'latest', 'revise') ?
     "allowed" : "denied";
// denied となります

echo $acl->isAllowed('editor', 'announcement', 'archive') ?
     "allowed" : "denied";
// denied となります

echo $acl->isAllowed('administrator', 'announcement', 'archive') ?
     "allowed" : "denied";
// denied となります

// 最新ニュースの改変拒否を staff (そして継承による marketing) から削除します
$acl->removeDeny('staff', 'latest', 'revise');

echo $acl->isAllowed('marketing', 'latest', 'revise') ?
     "allowed" : "denied";
// allowed となります

// ニュースレターの公開や保存の権限を、marketing から取り除きます
$acl->removeAllow('marketing',
                  'newsletter',
                  array('publish', 'archive'));

echo $acl->isAllowed('marketing', 'newsletter', 'publish') ?
     "allowed" : "denied";
// denied となります

echo $acl->isAllowed('marketing', 'newsletter', 'archive') ?
     "allowed" : "denied";
// denied となります

// marketing に対して、最新のニュースへのアクセスを許可します
$acl->allow('marketing', 'latest');

echo $acl->isAllowed('marketing', 'latest', 'publish') ?
     "allowed" : "denied";
// allowed となります

echo $acl->isAllowed('marketing', 'latest', 'archive') ?
     "allowed" : "denied";
// allowed となります

echo $acl->isAllowed('marketing', 'latest', 'anything') ?
     "allowed" : "denied";
// allowed となります
