example.programlisting:
$rowset   = $bugs->fetchAll("bug_status = 'FIXED'");

$rowCount = count($rowset);

if ($rowCount > 0) {
    echo "見つかった行数は $rowCount です";
} else {
    echo 'クエリにマッチする行がありません';
}

$bugs = new Bugs();

// テーブルのすべてのレコードを取得します
$rowset = $bugs->fetchAll();

foreach ($rowset as $row) {

    // 出力は 'Zend_Db_Table_Row' あるいはそれに似たものとなります
    echo get_class($row) . "\n";

    // 行のカラムを読み込みます
    $status = $row->bug_status;

    // 現在の行のカラムの値を変更します
    $row->assigned_to = 'mmouse';

    // 変更をデータベースに書き出します
    $row->save();
}

$bugs = new Bugs();

// テーブルのすべてのレコードを取得します
$rowset = $bugs->fetchAll();

// イテレータを 9 番目の要素に移動します (最初の要素がゼロです)
$rowset->seek(8);

// それを取得します
$row9 = $rowset->current();

// そして使用します
$row9->assigned_to = 'mmouse';
$row9->save();

sect2.programlisting:
$bugs = new Bugs();

// テーブルのすべてのレコードを取得します
$rowset = $bugs->fetchAll();

// 9 番目の要素を取得します
$row9->getRow(8);

// そして使用します
$row9->assigned_to = 'mmouse';
$row9->save();

example.programlisting:
$bugs   = new Bugs();
$rowset = $bugs->fetchAll();

$rowsetArray = $rowset->toArray();

$rowCount = 1;
foreach ($rowsetArray as $rowArray) {
    echo "row #$rowCount:\n";
    foreach ($rowArray as $column => $value) {
        echo "\t$column => $value\n";
    }
    ++$rowCount;
    echo "\n";
}

$bugs   = new Bugs();
$rowset = $bugs->fetchAll();

// オブジェクトをシリアライズします
$serializedRowset = serialize($rowset);

// これで、$serializedRowset をファイルなどに書き出すことができます

$rowsetDisconnected = unserialize($serializedRowset);

// これでオブジェクトのプロパティを使用できますが、読み込み専用です
$row = $rowsetDisconnected->current();
echo $row->bug_description;

$rowset = unserialize($serializedRowset);

$bugs = new Bugs();

// この行セットをテーブルに再接続し、
// データベースとの接続を復活させます
$rowset->setTable($bugs);

$row = $rowset->current();

// これで、行の内容を変更して保存できます
$row->bug_status = 'FIXED';
$row->save();

class MyRowset extends Zend_Db_Table_Rowset_Abstract
{
    // ...独自の処理
}

// 独自の行セットを、テーブルクラスの全インスタンスで
// デフォルトとして使用するように設定します
class Products extends Zend_Db_Table_Abstract
{
    protected $_name = 'products';
    protected $_rowsetClass = 'MyRowset';
}

// あるいは、テーブルクラスの特定のインスタンスでのみ
// 独自の行セットクラスを使用するように設定します
$bugs = new Bugs(array('rowsetClass' => 'MyRowset'));

class MyBugsRowset extends Zend_Db_Table_Rowset_Abstract
{
    /**
     * 現在の行セットのなかで、'updated_at' カラムの値が
     * 最大である行を見つけます
     */
    public function getLatestUpdatedRow()
    {
        $max_updated_at = 0;
        $latestRow = null;
        foreach ($this as $row) {
            if ($row->updated_at > $max_updated_at) {
                $latestRow = $row;
            }
        }
        return $latestRow;
    }
}

class Bugs extends Zend_Db_Table_Abstract
{
    protected $_name = 'bugs';
    protected $_rowsetClass = 'MyBugsRowset';
}
