example.programlisting:
$config = new Zend_Config_Xml('/path/to/config.xml', 'staging');

echo $config->database->params->host;   // prints "dev.example.com"
echo $config->database->params->dbname; // prints "dbname"

note.programlisting:
$string = <<<EOT <?xml version="1.0"?> <config xmlns:zf="http://framework.zend.com/xml/zend-config-xml/1.0/"> <production> <includePath> <zf:const zf:name="APPLICATION_PATH"/>/library</includePath> <db> <adapter value="pdo_mysql"/> <params> <host value="db.example.com"/> </params> </db> </production> <staging zf:extends="production"> <db> <params> <host value="dev.example.com"/> </params> </db> </staging> </config> EOT;
define('APPLICATION_PATH', dirname(__FILE__)); $config = new Zend_Config_Xml($string, 'staging');
echo $config->includePath; // Prints "/var/www/something/library"
