// Cria um novo documento PDF
$pdf1 = new Zend_Pdf();

// Carrega um documento PDF a partir de um arquivo
$pdf2 = Zend_Pdf::load($fileName);

// Carrega um documento PDF a partir de uma string
$pdf3 = Zend_Pdf::parse($pdfString);
